  render() {
    return (
      <div>
        <header>
          <h1> Enter Details</h1>
          {/* <button onClick={() => this.callAPI()}> Create Account </button> */}

        </header>


          <input
            type="text"
            name='accountName'
            value={this.state.form.accountName}
            onChange={this.handleInputChange}


          />
          <button type='submit' onClick = {this.handleSubmit}> Create Account</button>


      </div>
    )
  }

}

export default App;
